import React, { useState } from 'react';
import './App.css';

function PigGame() {
  const [level, setLevel] = useState(0);
  const [size, setSize] = useState(100);
  const [defaultImage] = useState('data:image/jpeg;base64,');
  const [customImage] = useState('data:image/jpeg;base64,'); 

  const feedPig = (foodValue) => {
    setLevel(level + foodValue);
    setSize(size + foodValue);
  };

  return (
    <div className="App">
      <h1>Level: {level}</h1>
      <img 
        src={level > 100 ? customImage : defaultImage} 
        alt="หมู" 
        style={{ width: `${size}px`, height: `${size}px` }} 
      />
      <div className="food-buttons">
        <button className="food-button" onClick={() => feedPig(5)}>
          <img src="data:image/jpeg;base64," alt="แตงโม" className="food-icon" />
          <div>5power</div>
        </button>
        <button className="food-button" onClick={() => feedPig(10)}>
          <img src="data:image/jpeg;base64," alt="ฟักทอง" className="food-icon" />
          <div>10power</div>
        </button>
        <button className="food-button" onClick={() => feedPig(20)}>
          <img src="data:image/jpeg;base64," alt="หญ้า" className="food-icon" />
          <div>20power</div>
        </button>
      </div>
    </div>
  );
}

export default PigGame;
